#include<iostream>
#include<string>
#include<cstdlib>
#include<cstring>

using namespace std;

int * Hashify(string source){
	int * arr = new int[26];
	memset(arr, 0, sizeof(int) * 26);
	int i;
	cout<<endl;
	for(i=0;i<source.length();i++){
		arr[source[i]-97]+=1;
	}

	for(i=0;i<28;i++){
		cout<<arr[i]<<" ";
	}
	return arr;
}
int number_needed(string a, string b) {
   int *hash_a = Hashify(a);
   int *hash_b = Hashify(b);
   int i=0;
   int to_delete = 0;
   int difference = 0;

   for(i=0;i<28;i++){
   	difference = abs(hash_a[i]-hash_b[i]);
   	//cout<<hash_a[i]<<" - "<<hash_b[i]<<" = "<< difference<<endl;
   	if( difference > 0 )
   		to_delete+=difference;
   		
   }

   return to_delete;
}

int main(){
	string a = "";
	string b = "";
	cout << number_needed(b, a) << endl;
	return 0;
}
